import pymorphy2

alphabet = 'абвгдеёжзийклмнопрстуфхцчшщьыъэюя'
alphabet2 = 'abcdefghijklmnopqrstuvwxyz'
punctuation = '.,?!;:'

morph = pymorphy2.MorphAnalyzer()

# Формат data: [<слово>, <знак препинания после него>]

def LettersIntoInputWeights(text):
	weights = []
	for l in text:
		num = 0
		if l in alphabet:
			num = (alphabet.rindex(l) + 1) / 33
		elif l in alphabet2:
			num = (alphabet2.rindex(l) + 1) / 26
		weights.append(num)

def DeleteUsefulInformation(data):
	for word in data:
		word[1] = ''
	return data

def TextIntoData(text):
	# Start
	data = []
	word = ''
	sign = ''
	# Main convert into data
	for l in text:
		if l == ' ':
			data.append([word, sign])
			word = sign = ''
		elif l in punctuation:
			sign = l
		else:
			word += l
	return data

def Lemmatization(data):
	for word in data:
		word[0] = morph.parse(word[0])[0].normal_form
	return data

def DataIntoText(data):
	text = ''
	for word in data:
		text += word[0] + word[1] + ' '
	return text
